import React, { useState, useEffect } from 'react';
import {
  TouchableOpacity,
  Text,
  StyleSheet,
  TextInput,
  ScrollView,
  ActivityIndicator,
  View,
  Button,
  Image,
  Platform
} from 'react-native';
//Importing the icon family
import { Entypo } from '@expo/vector-icons';
import { AntDesign } from '@expo/vector-icons';

import { ProgressBar, Colors } from 'react-native-paper';

// Importing the react native drop down picker
import { Picker } from 'react-native'

//Importing Device API to check the device type to render different content for android and ios
import * as Device from 'expo-device';

import alt from "../assets/alter.png";

//Importing Image Picker 
import * as ImagePicker from 'expo-image-picker';

import "firebase/storage";

import firebase from '../database/firebaseDb';

let picker_Items = [
  {
    label: "Company 1",
    value: 1
  },
  {
    label: "Company 2",
    value: 2
  },
  {
    label: "Company 3",
    value: 3
  },
  {
    label: "Company 4",
    value: 4
  },
  {
    label: "Company 5",
    value: 5
  },
  {
    label: "Company 6",
    value: 6
  },
  {
    label: "Company 7",
    value: 7
  },
  {
    label: "Company 8",
    value: 8
  },
  {
    label: "Company 9",
    value: 9
  },
  {
    label: "Company 10",
    value: 10
  }
]

function AddUserScreen({ navigation }) {

  const [isLoading, setIsLoading] = useState(false);

  const [pickerItems, setPickerItems] = useState(picker_Items);

  const [visibility, setVisibility] = useState(false);

  const [imageUri, setImageUri] = useState(null);


  const device_name = Device.osName;

  const [agency, setAgency] = useState('');
  const [brand, setBrand] = useState('');
  const [representative_name, setRepresentative_name] = useState('');
  const [image, setImage] = useState('')

  const [picked, setPicked] = useState(1);
  const [pickedValue, setPickedValue] = useState('');

  const [selected_brand_count, set_selected_brand_count] = useState(0);

  //Show Agency Add
  const [showAgencyAdd, setShowAgencyAdd] = useState(false);

  useEffect(() => {
    if (isLoading) {
      return (
        <View style={styles.preloader}>
          <ActivityIndicator size="large" color="#9E9E9E" />
        </View>
      )
    }
    for (let i = 0; i < pickerItems.length; i++) {
      if (pickerItems[i].value == picked) {
        console.log("The Picked item label is ==> ", pickerItems[i].label);
        setPickedValue(pickerItems[i].label);
      }
    }
    console.log("Agency name is : ", agency);
    console.log("Picker Items is ==> ", pickerItems);

    //Retrieving the data from firestore for purpose of counter 
    if (true) {
      const db = firebase.firestore();
      db.collection(`Data/Projects/${signedInUserData.email}`)
        .get()
        .then(snapshot => {
          let data = [];
          snapshot.forEach(element => {
            data.push(Object.assign({
              id: element.id,
              "ProjectName": element.ProjectName,
              "ProjectMembers": element.ProjectMembers,
              "ProjectStages": element.ProjectStages,
              "ProjectTasks": element.ProjectTasks,
              "createAt": element.createAt,
            }, element.data()))
          })
          console.log("data=> ", data)
          ///////////////////////////////Here is the code for sending notifications
          ///////////////////////////////Here is the code for sending notifications
          if (initial == true) {
            let dateArray = [];
            for (let i = 0; i < data.length; i++) {
              var date = new Date(data[i].ProjectEndingDate);
              var currentDate = new Date();
              var difference_in_seconds = date - currentDate;
              var Difference_In_Days = difference_in_seconds / (1000 * 3600 * 24);
              if (Difference_In_Days < 25) {
                console.log(`The Project is less than ${Difference_In_Days} days and its name is ${data[i].ProjectName}`)
                ////////////////////////////
                let today = new Date();
                let date = today.getFullYear() + '-' + (today.getMonth() + 1) + '-' + today.getDate();
                let time = today.getHours() + ":" + today.getMinutes() + ":" + today.getSeconds();
                let dateTime = date + ' ' + time;
                dateTime = dateTime.toString();
                ////////////////////////////
                let thingsRef = db.collection(`Data/Notifications/${signedInUserData.email}`);
                thingsRef.add({
                  uid: signedInUserData.uid,
                  userEmail: signedInUserData.email,
                  ProjectName: data[i].ProjectName,
                  ProjectMembers: data[i].ProjectMembers,
                  ProjectStartingDate: data[i].ProjectStartingDate,
                  ProjectEndingDate: data[i].ProjectEndingDate,
                  CurrentStage: data[i].CurrentStage,
                  CurrentStageCurrentTask: data[i].CurrentStageCurrentTask,
                  createAt: data[i].createAt,
                  notificationSentAt: dateTime,
                  DaysLeft: Difference_In_Days
                }).then(() => {
                  //alert(`The '${Difference_In_Days}' Days are remaining of Project whose name is '${data[i].ProjectName}'`);
                })
              }
              let temp = {
                "ProjectEndingDate": Difference_In_Days + " days",
                "ProjectName": data[i].ProjectName
              }
              //console.log(data[i].ProjectEndingDate+'\n');
              dateArray.push(temp);
            }
          }

          ///////////////////////////////Here is the code for sending notifications
          ///////////////////////////////Here is the code for sending notifications

          if (firestoreData.length != data.length) {
            setFirestoreData(data);
