      setPickerItems(
        [
          ...pickerItems,
          {
            label: agency,
            value: length + 1
          }
        ]
      );
      setShowAgencyAdd(false);
      alert("Agency Added");
    }
    else {
      alert("Please enter an agency name to add it to the list.");
    }
  }

  const storeUser = () => {
    if (pickedValue === '' || brand === '' || representative_name === '' || imageUri === '') {
      alert('Fill all the fields!');
